            name,
            interval.toString,
            n.toString)
          timers.startTimerWithFixedDelay(Increment, interval)
          Behaviors.same
        case Increment =>
          val newValue = n + 1
          context.log.debug2("[{}] Incremented counter to [{}]", name, newValue)
          counter(name, timers, newValue)
        case GetValue(replyTo) =>
          replyTo ! Value(n)
          Behaviors.same
      }
    }
