    if (List(4, 5).contains(n)) throw new RuntimeException(s"Boom! Bad value found: $n")
    else n.toString)
  .recover {
    case e: RuntimeException => e.getMessage
  }
  .runForeach(println) Java copysourceSource.from(Arrays.asList(0, 1, 2, 3, 4, 5, 6))
