  val zero = new util.ArrayList[Int]()
  Flow[Int].fold(zero) { (list, element) =>
    list.add(element)
    list
  }
}
val stream =
  Source(1 to 3).via(mutableFold).to(Sink.foreach(println))

stream.run()
stream.run()
stream.run()
// prints:
// [1, 2, 3]
// [1, 2, 3]
// [1, 2, 3]
