    Flow.lazyFlow(
        () -> {
          List<Integer> zero = new ArrayList<>();

          return Flow.of(Integer.class)
              .fold(
                  zero,
                  (list, element) -> {
                    list.add(element);
                    return list;
                  });
        });

RunnableGraph<NotUsed> stream =
    Source.range(1, 3).via(mutableFold).to(Sink.foreach(System.out::println));

stream.run(system);
stream.run(system);
stream.run(system);
// prints:
// [1, 2, 3]
// [1, 2, 3]
